			this.setState({
				selmaPos: { ...this.state.selmaPos, positionSelmaX: newPosX, positionSelmaY: newPosY }
			});
			i++;
			if (i >= this.state.selmaPos.SelmaMovX.length) {
				i = 0;
			}
		}, 1000);
	};

