import React from "react";
import config from "./configSpringfieldVice.json";

function MovingObs(props) {
	const obstacles = this.props.positionMovingObsX.map((obj) => {
		const movingObszone = {
			width: "30px",
			height: "30px",
			backgroundColor: "white",
			borderRadius: "50%",
			position: "absolute",
			left: obj.positionMovingObsX + props.obstaclePosition,
			top: obj.positionMovingObsY,
			transform: "scale(" + obj.positionMovingObsY * config.movingObs.scale + ")",
			transition: "0.8s"
		};
		//console.log(this.props.positionMovingObsX, this.props.positionMovingObsY);
		//console.log("movingObszone = ", movingObszone);

		const styleMovingObs = {
			width: "80px",
			height: "auto",
			position: "absolute",
			left: "-20px",
			top: "-20px",
			margin: 0,
			padding: 0
		};

		return (
			<div>
				<div id="MovingObs" style={movingObszone}>
					<div style={styleMovingObs} />
				</div>
			</div>
		);
	});

	return <div>{obstacles}</div>;
}
export default MovingObs;
