import React, { Component } from "react";
import donut from "./img/donut.png";
import "./FlyingDonut.css";

class FlyingDonut extends Component {
	constructor(props) {
		super(props);
		this.state = {};
	}

	render() {
		const donutStyle = {
			position: "absolute",
			transitionDuration: "0.5s",
			width: "35px",
			left: "0px",
			display: this.props.display ? "block" : "none",
			left: this.props.throwing ? "600px" : "0px"
		};

		return (
			<div>
				<img src={donut} style={donutStyle} />
			</div>
		);
	}
}
export default FlyingDonut;
